import FlashMessageManager from "./FlashMessageManager";
import FlashMessageWrapper, { styleWithInset } from "./FlashMessageWrapper";
import FlashMessage, {
  DefaultFlash,
  positionStyle,
  showMessage,
  hideMessage,
  FlashMessageTransition,
} from "./FlashMessage";

export {
  FlashMessageManager,
  DefaultFlash,
  styleWithInset,
  positionStyle,
  showMessage,
  hideMessage,
  FlashMessageTransition,
};

export default FlashMessage;
